mapper.update:
UPDATE faq_medicalrec_ca SET name = #{name},
id_par = #{idPar},
operator = #{operator}, gmt_modify = now() WHERE id_medicalrec_ca = #{idMedicalrecCa}

UPDATE faq_medicalrec SET name = #{name}, id_demo = #{idDemo},
id_medicalrec_ca = #{idMedicalrecCa},
sd_level = #{sdLevel}, sd_use = #{sdUse}, fg_active = #{fgActive}, operator = #{operator}, gmt_modify = now() WHERE id_medicalrec = #{idMedicalrec}

UPDATE faq_medicalrec_ca SET fg_valid = #{status}, operator = #{operator}, gmt_modify = now() WHERE id_medicalrec_ca in (
#{item}
)

UPDATE faq_medicalrec SET
fg_active = #{status},
fg_valid = #{status},
operator = #{operator}, gmt_modify = now() WHERE id_medicalrec in (
#{item}
)

mapper.select:
SELECT fm.id_medicalrec, fm.name, fm.id_demo, fm.id_medicalrec_ca, fm.id_org, fm.sd_level, fm.sd_use, fm.count, fm.fg_active, fm.fg_valid, fm.creator, fm.gmt_create, fm.operator, fm.gmt_modify, fmc.name as idMedicalrecCaText, bd.name as chtName, so.name as orgName FROM faq_medicalrec fm left join faq_medicalrec_ca fmc on fm.id_medicalrec_ca = fmc.id_medicalrec_ca left join bas_demo bd on fm.id_demo = bd.id_demo left join sys_org so on fm.id_org = so.id_org WHERE fm.fg_valid = '0' AND fm.id_medicalrec_ca = #{idMedicalrecCa}
AND fm.name like concat('%',#{name},'%')
limit #{offset}, #{limit}
