mapper.update:
UPDATE bas_body_ca SET fg_valid = #{status}, operator = #{operator}, gmt_modify = now() WHERE id_body_ca in (
#{item}
)

UPDATE bas_body SET
fg_active = #{status},
fg_valid = #{status}, fg_active = '0',
operator = #{operator}, gmt_modify = now() WHERE id_body in (
#{item}
)

UPDATE bas_body_result SET fg_valid = #{status}, fg_active = '0', operator = #{operator}, gmt_modify = now() WHERE id_result in (
#{item}
)

mapper.select:
SELECT bb.id_body, bb.des_body, bb.sd_body, bb.id_body_ca, bbc.name as idBodyCaText, bb.cd_check, bb.fg_active, bb.fg_valid, bb.creator, bb.gmt_create, bb.operator, bb.gmt_modify FROM bas_body bb left join bas_body_ca bbc on bb.id_body_ca = bbc.id_body_ca WHERE bb.fg_valid = '0' AND bb.id_body_ca = #{idBodyCa}
AND bb.des_body like concat('%',#{desBody},'%')
limit #{offset}, #{limit}

SELECT bbc.name AS idBodyText, bb.id_body_ca, bb.id_body, bb.des_body, bb.sd_body, bb.cd_check, bbr.id_result, bbr.des_result, bbr.id_media, bbr.fg_reason, bbr.fg_back, bbr.des_expert, bm.sd_type, bm.path FROM bas_body bb LEFT JOIN bas_body_result bbr ON bb.id_body = bbr.id_body LEFT JOIN bas_body_ca bbc ON bb.id_body_ca = bbc.id_body_ca left join bas_media bm on bbr.id_media = bm.id_media WHERE bb.fg_active = '1' AND (bbr.fg_active = '1' or bbr.fg_active is null)
AND (bbc.name like concat('%',#{keywords},'%') or bb.des_body like concat('%',#{keywords},'%'))
limit #{offset}, #{limit}
