mapper.select:
SELECT count(1) FROM bas_demo WHERE fg_valid = '0'
AND id_demo_ca = #{idDemoCa}
AND name like concat('%',#{name},'%')

SELECT bd.id_demo, bd.name, bd.id_demo_ca, bd.fg_active, bd.fg_valid, bd.creator, bd.gmt_create, bd.operator, bd.gmt_modify, bdc.name as idDemoCaText FROM bas_demo bd left join bas_demo_ca bdc on bd.id_demo_ca = bdc.id_demo_ca WHERE bd.fg_valid = '0'
AND bd.id_demo_ca = #{idDemoCa}
AND bd.name like concat('%',#{name},'%')
limit #{offset}, #{limit}

select bmt.id_tag, bmt.name, bmt.id_media, bmt.id_demo, bmt.cd_med_asse, bmt.fg_show_make, bmt.fg_show_exec, bmt.fg_active, bmt.fg_valid, bmt.sort, bmt.creator, bmt.gmt_create, bmt.operator, bmt.gmt_modify, bm.path, bmt.sd_process, bmt.process_serialno from bas_medical_tag bmt left join bas_media bm on bmt.id_media = bm.id_media where bmt.fg_valid = '0' and bmt.id_demo = #{idDemo}
and bmt.fg_show_exec = #{fgShowExec}
order by bmt.sort

SELECT bi.id_inques, bi.des_inques, bi.id_inques_ca,bic.name as idInquesCaText FROM faq_med_case_inques_list bi left join bas_inques_ca bic on bi.id_inques_ca = bic.id_inques_ca WHERE bi.id_med_case = #{extId} and bi.fg_valid = '0'
AND bi.id_inques_ca = #{idInquesCa}
AND bi.des_inques like concat('%',#{desInques},'%')
AND (bi.des_inques like concat('%',#{keywords},'%') or bic.name like concat('%',#{keywords},'%'))
limit #{offset}, #{limit}

SELECT bb.id_body, bb.des_body, bb.sd_body, bb.id_body_ca, bbc.name as idBodyCaText, bb.cd_check FROM faq_med_case_body_list bb left join bas_body_ca bbc on bb.id_body_ca = bbc.id_body_ca WHERE bb.id_med_case = #{extId} and bb.fg_valid = '0'
AND bb.id_body_ca = #{idBodyCa}
AND bb.des_body like concat('%',#{desBody},'%')
AND (bbc.name like concat('%',#{keywords},'%') or bb.des_body like concat('%',#{keywords},'%'))
limit #{offset}, #{limit}

SELECT bii.id_inspect_item, bii.na_item, bii.id_inspect, bii.na_short, bii.des_stand, bic.name as idInspectText FROM faq_med_case_inspect_list bii left join bas_inspect_ca bic on bii.id_inspect = bic.id_inspect WHERE bii.id_med_case = #{extId} and bii.fg_valid = '0'
AND bii.id_inspect = #{idInspect}
AND bii.na_item like concat('%',#{naItem},'%')
AND (bic.name like concat('%',#{keywords},'%') or bii.na_item like concat('%',#{keywords},'%'))
limit #{offset}, #{limit}
