mapper.select:
SELECT id_die, name, cd_dieclass, icd, pinyin, fg_active, fg_valid, sort, creator, gmt_create, operator, gmt_modify FROM bas_die WHERE fg_valid = '0'
AND name like concat('%',#{name},'%')
AND fg_active = #{fgActive}
AND icd like concat('%',#{icd},'%')
AND pinyin like concat('%',#{pinyin},'%')
limit #{offset}, #{limit}
