mapper.select:
SELECT id_drugs, name, cd_drugsclass, spec, unit, pinyin, fg_active, fg_valid, sort, creator, gmt_create, operator, gmt_modify FROM bas_drugs WHERE fg_valid = '0'
AND name like concat('%',#{name},'%')
AND fg_active = #{fgActive}
AND pinyin like concat('%',#{pinyin},'%')
AND (name like concat('%',#{keywords},'%') or pinyin like concat('%',#{keywords},'%'))
limit #{offset}, #{limit}

SELECT id_drugsclass, cd AS id , cd_par AS pId , name, fg_active FROM bas_drugs_class WHERE fg_valid = '0'
and cd_par = #{id}
and (cd_par is null or cd_par = '')
