mapper.update:
UPDATE bas_inques_ca SET fg_valid = #{status}, fg_active = '0', operator = #{operator}, gmt_modify = now() WHERE id_inques_ca in (
#{item}
)

UPDATE bas_inques SET des_inques = #{desInques},
fg_active = #{fgActive},
operator = #{operator}, gmt_modify = now() WHERE id_inques = #{idInques}

UPDATE bas_inques SET
fg_active = #{status},
fg_valid = #{status}, fg_active = '0',
operator = #{operator}, gmt_modify = now() WHERE id_inques in (
#{item}
)

UPDATE bas_inques_answer SET des_answer = #{desAnswer}, id_inques = #{idInques},
id_media = #{idMedia},
fg_reason = #{fgReason}, fg_back = #{fgBack}, des_expert = #{desExpert}, fg_tag = #{fgTag}, fg_active = #{fgActive}, operator = #{operator}, gmt_modify = now() WHERE id_answer = #{idAnswer}

UPDATE bas_inques_answer SET fg_valid = #{status}, fg_active = '0', operator = #{operator}, gmt_modify = now() WHERE id_answer in (
#{item}
)

mapper.select:
SELECT bi.id_inques, bi.des_inques, bi.id_inques_ca,bic.name as idInquesCaText, bi.fg_active, bi.fg_valid, bi.creator, bi.gmt_create, bi.operator, bi.gmt_modify FROM bas_inques bi left join bas_inques_ca bic on bi.id_inques_ca = bic.id_inques_ca WHERE bi.fg_valid = '0' AND bi.id_inques_ca = #{idInquesCa}
AND bi.des_inques like concat('%',#{desInques},'%')
limit #{offset}, #{limit}

SELECT bic.id_inques_ca, bic.name AS idInquesCaText , bi.id_inques , bi.des_inques , bia.id_answer , bia.des_answer FROM bas_inques bi LEFT JOIN bas_inques_answer bia ON bi.id_inques = bia.id_inques LEFT JOIN bas_inques_ca bic ON bi.id_inques_ca = bic.id_inques_ca WHERE bi.fg_active = '1' AND bia.fg_active = '1'
AND (bic.name like concat('%',#{keywords},'%') or bi.des_inques like concat('%',#{keywords},'%'))
limit #{offset}, #{limit}
