mapper.update:
UPDATE faq_eva_case SET cd_eva_asse = #{cdEvaAsse}, name = #{name}, descript = #{descript}, fg_group = #{fgGroup}, score_upper = #{scoreUpper}, score_lower = #{scoreLower}, score_default = #{scoreDefault},
count = #{count},
fg_active = #{fgActive}, operator = #{operator}, gmt_modify = now() WHERE id_eva_case = #{idEvaCase}

UPDATE faq_eva_case SET
fg_active = #{status},
fg_valid = #{status}, fg_active = '0',
operator = #{operator}, gmt_modify = now() WHERE id_eva_case in (
#{item}
)

mapper.select:
SELECT count(1) FROM faq_eva_case fec LEFT JOIN sys_org so on fec.id_org = so.id_org WHERE fec.fg_valid = '0'
AND fec.cd_eva_asse = #{cdEvaAsse}
AND fec.name like concat('%',#{name},'%')
AND fec.creator like concat('%',#{creator},'%')
AND so.fg_plat = #{fgPlat}
AND so.name = #{orgName}

SELECT fec.id_eva_case, fec.cd_eva_asse, fec.name, fec.descript, fec.fg_group, fec.score_upper, fec.score_lower, fec.score_default, fec.id_org, fec.count, fec.fg_active, fec.fg_valid, fec.creator, fec.gmt_create, fec.operator, fec.gmt_modify, so.name as orgName, so.fg_plat FROM faq_eva_case fec LEFT JOIN sys_org so on fec.id_org = so.id_org WHERE fec.fg_valid = '0'
AND fec.cd_eva_asse = #{cdEvaAsse}
AND fec.name like concat('%',#{name},'%')
AND fec.creator like concat('%',#{creator},'%')
AND so.fg_plat = #{fgPlat}
AND so.name = #{orgName}
limit #{offset}, #{limit}
