mapper.select:
SELECT count(1) FROM faq_med_case fmc LEFT JOIN sys_org so on fmc.id_org = so.id_org WHERE fmc.fg_valid = '0' and fmc.fg_public = '1'
AND fmc.cd_med_asse = #{cdMedAsse}
AND fmc.name like concat('%',#{name},'%')
AND fmc.creator like concat('%',#{creator},'%')
AND so.fg_plat = #{fgPlat}
AND so.name = #{orgName}
AND fmc.id_org = #{idOrg}

SELECT fmc.id_med_case,fmc.name, fmc.descript, fmc.cd_med_asse, fmc.id_org, fmc.count, fmc.fg_active, fmc.fg_valid, fmc.creator, fmc.gmt_create, fmc.operator, fmc.gmt_modify, so.name as orgName, so.fg_plat, bma.script FROM faq_med_case fmc LEFT JOIN sys_org so on fmc.id_org = so.id_org left join bas_med_asse bma on fmc.cd_med_asse = bma.cd_med_asse and bma.fg_valid='0' WHERE fmc.fg_valid = '0' and fmc.fg_public = '1'
AND fmc.cd_med_asse = #{cdMedAsse}
AND fmc.name like concat('%',#{name},'%')
AND fmc.creator like concat('%',#{creator},'%')
AND so.fg_plat = #{fgPlat}
AND so.name = #{orgName}
AND fmc.id_org = #{idOrg}
limit #{offset}, #{limit}

SELECT count(1) FROM faq_med_case_inques_list fmci WHERE fmci.id_med_case = #{idMedCase} AND fmci.fg_valid = '0'
AND fmci.des_inques like concat('%',#{keyword},'%')
AND fmci.id_inques_ca = #{idInquesCa}

SELECT fmci.id_med_case_list, fmci.id_med_case, fmci.id_inques_ca, fmci.id_inques, fmci.des_inques, fmci.id_answer, fmci.des_answer, fmci.is_masculine, fmci.id_media, fmci.fg_reason, fmci.fg_back, fmci.des_expert, fmci.fg_carried, bm.sd_type, bm.path, fmci.sd_inques_label FROM faq_med_case_inques_list fmci LEFT join bas_media bm on fmci.id_media = bm.id_media WHERE fmci.id_med_case = #{idMedCase} AND fmci.fg_valid = '0'
AND fmci.des_inques like concat('%',#{keyword},'%')
AND fmci.id_inques_ca = #{idInquesCa}
limit #{offset}, #{limit}

SELECT count(1) FROM faq_med_case_inspect_list fmci WHERE fmci.id_med_case = #{idMedCase} AND fmci.fg_valid = '0'
AND fmci.na_item like concat('%',#{keyword},'%')
AND fmci.id_inspect = #{idInspect}

SELECT fmci.id_med_case_list,fmci.id_med_case, fmci.id_inspect, fmci.id_inspect_item, fmci.na_item, fmci.na_short, fmci.des_stand, fmci.id_result,fmci.is_masculine, fmci.val_result, fmci.des_result, fmci.id_media, fmci.cost_money, fmci.cost_time, fmci.fg_show, fmci.fg_reason, fmci.fg_back, fmci.des_expert, fmci.fg_carried FROM faq_med_case_inspect_list fmci WHERE fmci.id_med_case = #{idMedCase} AND fmci.fg_valid = '0'
AND fmci.na_item like concat('%',#{keyword},'%')
AND fmci.id_inspect = #{idInspect}
limit #{offset}, #{limit}

SELECT count(1) FROM faq_med_case_body_list fmcbl WHERE fmcbl.id_med_case = #{idMedCase} AND fmcbl.fg_valid = '0'
AND fmcbl.des_body like concat('%',#{keyword},'%')
AND fmcbl.id_body_ca = #{idBodyCa}

SELECT fmcbl.id_med_case_list, fmcbl.id_med_case, fmcbl.id_body_ca, fmcbl.id_body, fmcbl.des_body, fmcbl.sd_body, fmcbl.cd_check, fmcbl.id_result, fmcbl.des_result, fmcbl.id_media, fmcbl.fg_reason, fmcbl.fg_back, fmcbl.des_expert, fmcbl.fg_carried, fmcbl.fg_valid FROM faq_med_case_body_list fmcbl WHERE fmcbl.id_med_case = #{idMedCase} AND fmcbl.fg_valid = '0'
AND fmcbl.des_body like concat('%',#{keyword},'%')
AND fmcbl.id_body_ca = #{idBodyCa}
limit #{offset}, #{limit}

