mapper.update:
UPDATE faq_med_case SET
fg_active = #{status},
fg_valid = #{status},
operator = #{operator}, gmt_modify = now() WHERE id_med_case in (
#{item}
)

mapper.select:
SELECT count(1) FROM faq_med_case fmc LEFT JOIN sys_org so on fmc.id_org = so.id_org WHERE fmc.fg_valid = '0'
AND fmc.cd_med_asse = #{cdMedAsse}
AND fmc.name like concat('%',#{name},'%')
AND fmc.creator like concat('%',#{creator},'%')
AND so.fg_plat = #{fgPlat}
AND so.name = #{orgName}

SELECT fmc.id_med_case,fmc.name, fmc.descript, fmc.cd_med_asse, fmc.id_org, fmc.count, fmc.fg_active, fmc.fg_valid, fmc.creator, fmc.gmt_create, fmc.operator, fmc.gmt_modify, so.name as orgName, so.fg_plat FROM faq_med_case fmc LEFT JOIN sys_org so on fmc.id_org = so.id_org WHERE fmc.fg_valid = '0'
AND fmc.cd_med_asse = #{cdMedAsse}
AND fmc.name like concat('%',#{name},'%')
AND fmc.creator like concat('%',#{creator},'%')
AND so.fg_plat = #{fgPlat}
AND so.name = #{orgName}
limit #{offset}, #{limit}
