mapper.update:
UPDATE exm_testplan SET na_testplan = #{naTestplan}, des_testplan = #{desTestplan}, id_testpaper = #{idTestpaper},
sd_testplan = #{sdTestplan},
fg_active = #{fgActive}, operator = #{operator}, gmt_modify = now() WHERE id_testplan = #{idTestplan}

UPDATE exm_testplan SET
fg_active = #{status},
fg_valid = #{status}, fg_active = '0',
operator = #{operator}, gmt_modify = now() WHERE id_testplan in (
#{item}
)

mapper.select:
SELECT a.id_testplan,a.na_testplan, a.des_testplan, a.id_testpaper, a.id_org, a.sd_testplan, a.fg_active, a.fg_valid, a.creator, a.gmt_create, a.operator, a.gmt_modify, so.name as orgName, ifnull(b.na_testpaper, '自定义') as idTestpaperText FROM exm_testplan a left join sys_org so on a.id_org = so.id_org left join exm_testpaper b on a.id_testpaper = b.id_testpaper WHERE a.fg_valid = '0'
AND a.na_testplan like concat('%',#{naTestplan},'%')
limit #{offset}, #{limit}
