mapper.select:
SELECT count(1) FROM exm_testplan_detail a LEFT JOIN faq_med_tag b ON a.id_medicalrec = b.id_medicalrec LEFT JOIN faq_med_case c ON b.id_med_case = c.id_med_case inner JOIN faq_med_case_patient d ON d.id_med_case = b.id_med_case AND c.cd_med_asse = '003' LEFT JOIN user_info e ON a.id_student = e.user_id LEFT JOIN exm_testplan f ON a.id_testplan = f.id_testplan LEFT JOIN exm_testpaper g ON f.id_testpaper = g.id_testpaper LEFT JOIN faq_medicalrec k ON a.id_medicalrec = k.id_medicalrec WHERE a.sd_testplan_detail IN('0' , '1') AND f.id_org = #{idOrg}
and k.name like concat(#{medicalrecName},'%')
and f.na_testplan like concat(#{naTestplan},'%')
and g.na_testpaper like concat(#{naTestpaper},'%')
and a.id_student = #{currentUserId}

SELECT a.id_testplan_detail, a.sd_testplan_detail as status, a.id_testplan , a.id_student , b.id_medicalrec, k.id_demo , d.id_med_case as idMedCase, d.name as patName, d.sex as patSex, d.age as patAge, d.complaint as complaint, e.real_name as distributeDoc, f.na_testplan , f.id_testpaper , g.na_testpaper FROM exm_testplan_detail a LEFT JOIN faq_med_tag b ON a.id_medicalrec = b.id_medicalrec LEFT JOIN faq_med_case c ON b.id_med_case = c.id_med_case inner JOIN faq_med_case_patient d ON d.id_med_case = b.id_med_case AND c.cd_med_asse = '003' LEFT JOIN user_info e ON a.id_student = e.user_id LEFT JOIN exm_testplan f ON a.id_testplan = f.id_testplan LEFT JOIN exm_testpaper g ON f.id_testpaper = g.id_testpaper LEFT JOIN faq_medicalrec k ON a.id_medicalrec = k.id_medicalrec WHERE a.sd_testplan_detail IN('0' , '1') AND f.id_org = #{idOrg}
and k.name like concat(#{medicalrecName},'%')
and f.na_testplan like concat(#{naTestplan},'%')
and g.na_testpaper like concat(#{naTestpaper},'%')
and a.id_student = #{currentUserId}
limit #{offset}, #{limit}

select
    a.id_testexec, a.dt_exec_begin as beginTime, a.dt_exec_end as endTime, b.id_testexec_result,
    a.sd_testexec, a.cur_serialno
from
    exm_testexec a
left join exm_med_result b on a.id_testexec = b.id_testexec and b.serial = 1
where


        b.id_testexec_result = #{idTestexecResult}


        a.id_testplan_detail = #{idTestplanDetail}
        and a.id_student=#{idStudent}

SELECT count(1) FROM faq_med_tag a LEFT JOIN bas_medical_tag b ON a.id_tag = b.id_tag LEFT JOIN faq_med_case_inques_list c ON c.id_med_case = a.id_med_case and c.fg_valid = '0' LEFT JOIN exm_med_result_inques d on c.id_med_case_list = d.id_med_case_list and d.fg_valid = '0' and d.id_testexec_result = #{idTestexecResult} WHERE a.id_medicalrec = #{idMedicalrec} AND b.cd_med_asse = #{cdMedAsse}
AND c.id_inques_ca = #{extItemId}
AND c.des_inques like concat('%',#{keyword},'%')
and c.sd_inques_label = #{sdInquesLabel} and c.id_inques_pre is null and c.id_inques_pre2 is null and c.id_inques_pre3 is null and c.id_inques_pre4 is null and c.id_inques_pre5 is null

SELECT c.id_med_case_list , c.id_inques , c.des_inques, c.is_masculine, IF(d.id_med_case_list is null,0,1) as extQa FROM faq_med_tag a LEFT JOIN bas_medical_tag b ON a.id_tag = b.id_tag LEFT JOIN faq_med_case_inques_list c ON c.id_med_case = a.id_med_case and c.fg_valid = '0' LEFT JOIN exm_med_result_inques d on c.id_med_case_list = d.id_med_case_list and d.fg_valid = '0' and d.id_testexec_result = #{idTestexecResult} WHERE a.id_medicalrec = #{idMedicalrec} AND b.cd_med_asse = #{cdMedAsse}
AND c.id_inques_ca = #{extItemId}
AND c.des_inques like concat('%',#{keyword},'%')
and c.sd_inques_label = #{sdInquesLabel} and c.id_inques_pre is null and c.id_inques_pre2 is null and c.id_inques_pre3 is null and c.id_inques_pre4 is null and c.id_inques_pre5 is null
order by c.id_med_case_list limit #{offset}, #{limit}

SELECT
    count(1)
FROM
    faq_med_tag a
LEFT JOIN bas_medical_tag b ON a.id_tag = b.id_tag
LEFT JOIN faq_med_case_inques_list c ON c.id_med_case = a.id_med_case and c.fg_valid = '0'
LEFT JOIN exm_med_result_inques d on c.id_med_case_list = d.id_med_case_list and d.fg_valid = '0'
and d.id_testexec_result = #{idTestexecResult}
WHERE
    a.id_medicalrec = #{idMedicalrec}
AND b.cd_med_asse = #{cdMedAsse}
AND(
    c.id_inques_pre = #{idInques}
    OR c.id_inques_pre2 = #{idInques}
    OR c.id_inques_pre3 = #{idInques}
    OR c.id_inques_pre4 = #{idInques}
    OR c.id_inques_pre5 = #{idInques}
)

SELECT
    c.id_med_case_list ,
    c.id_inques ,
    c.des_inques,
    c.is_masculine,
    IF(d.id_med_case_list is null,0,1) as extQa
FROM
    faq_med_tag a
LEFT JOIN bas_medical_tag b ON a.id_tag = b.id_tag
LEFT JOIN faq_med_case_inques_list c ON c.id_med_case = a.id_med_case and c.fg_valid = '0'
LEFT JOIN exm_med_result_inques d on c.id_med_case_list = d.id_med_case_list and d.fg_valid = '0'
and d.id_testexec_result = #{idTestexecResult}
WHERE
    a.id_medicalrec = #{idMedicalrec}
AND b.cd_med_asse = #{cdMedAsse}
AND(
    c.id_inques_pre = #{idInques}
    OR c.id_inques_pre2 = #{idInques}
    OR c.id_inques_pre3 = #{idInques}
    OR c.id_inques_pre4 = #{idInques}
    OR c.id_inques_pre5 = #{idInques}
)
order by
    c.id_med_case_list
limit #{offset}, #{limit}

SELECT
    count(1)
FROM
    faq_med_tag a
LEFT JOIN bas_medical_tag b ON a.id_tag = b.id_tag
LEFT JOIN faq_med_case_body_list c ON c.id_med_case = a.id_med_case AND c.fg_valid = '0'
LEFT JOIN bas_body_ca e ON c.id_body_ca = e.id_body_ca
LEFT JOIN exm_med_result_body d ON c.id_med_case_list = d.id_med_case_list AND d.fg_valid = '0'
and d.id_testexec_result = #{idTestexecResult}
left join bas_die f on d.id_die = f.id_die
WHERE
    a.id_medicalrec = #{idMedicalrec}
AND b.cd_med_asse = #{cdMedAsse}

    AND c.sd_body = #{sdBody}


    AND c.id_body_ca = #{extItemId}


    AND c.des_body like concat('%',#{keyword},'%')

SELECT c.id_med_case_list , c.des_body , c.id_body , c.cd_check , c.sd_body , e.name AS cdCheckText , IF(d.id_die = '',null,d.id_die) as id_die, IF(d.id_med_case_list IS NULL , 0 , 1) AS extQa FROM faq_med_tag a LEFT JOIN bas_medical_tag b ON a.id_tag = b.id_tag LEFT JOIN faq_med_case_body_list c ON c.id_med_case = a.id_med_case AND c.fg_valid = '0' LEFT JOIN bas_body_ca e ON c.id_body_ca = e.id_body_ca LEFT JOIN exm_med_result_body d ON c.id_med_case_list = d.id_med_case_list AND d.fg_valid = '0' and d.id_testexec_result = #{idTestexecResult} WHERE a.id_medicalrec = #{idMedicalrec} AND b.cd_med_asse = #{cdMedAsse}
AND c.sd_body = #{sdBody}
AND c.id_body_ca = #{extItemId}
AND c.des_body like concat('%',#{keyword},'%')
ORDER BY c.id_med_case_list limit #{offset}, #{limit}

SELECT count(1) FROM faq_med_tag a LEFT JOIN bas_medical_tag b ON a.id_tag = b.id_tag LEFT JOIN faq_med_case_inspect_list c ON c.id_med_case = a.id_med_case and c.fg_valid = '0' LEFT JOIN exm_med_result_inspect d on c.id_med_case_list = d.id_med_case_list and d.fg_valid = '0' and d.id_testexec_result = #{idTestexecResult} left join bas_die e on d.id_die = e.id_die WHERE a.id_medicalrec = #{idMedicalrec} AND b.cd_med_asse = #{cdMedAsse}
AND c.id_inspect = #{extItemId}
AND c.na_item like concat('%',#{keyword},'%')

SELECT c.id_med_case_list , c.na_item, c.is_masculine, c.id_inspect_item, IF(d.id_die = '',null,d.id_die) as id_die, e.name as idDieText, IF(d.id_med_case_list is null,0,1) as extQa FROM faq_med_tag a LEFT JOIN bas_medical_tag b ON a.id_tag = b.id_tag LEFT JOIN faq_med_case_inspect_list c ON c.id_med_case = a.id_med_case and c.fg_valid = '0' LEFT JOIN exm_med_result_inspect d on c.id_med_case_list = d.id_med_case_list and d.fg_valid = '0' and d.id_testexec_result = #{idTestexecResult} left join bas_die e on d.id_die = e.id_die WHERE a.id_medicalrec = #{idMedicalrec} AND b.cd_med_asse = #{cdMedAsse}
AND c.id_inspect = #{extItemId}
AND c.na_item like concat('%',#{keyword},'%')
order by c.id_med_case_list limit #{offset}, #{limit}

