mapper.select:
SELECT sr.role_id, sr.name , sr.resume , sr.state , sr.operator , sr.gmt_create FROM sys_role sr WHERE sr.is_deleted = 'N'
AND sr.name LIKE concat(#{conditionValue},'%')
AND sr.state = #{state} ORDER BY sr.role_id limit #{offset}, #{limit}
